INSERT INTO sources (id, title, content, category, created_at) VALUES
('source-1', 'בראשית א:א', 'בְּרֵאשִׁית בָּרָא אֱלֹהִים אֵת הַשָּׁמַיִם וְאֵת הָאָרֶץ', 'תנ"ך', NOW()),
('source-2', 'שמות כ:ב-ג', 'אָנֹכִי יְהוָה אֱלֹהֶיךָ אֲשֶׁר הוֹצֵאתִיךָ מֵאֶרֶץ מִצְרַיִם מִבֵּית עֲבָדִים. לֹא יִהְיֶה לְךָ אֱלֹהִים אֲחֵרִים עַל פָּנָי', 'תנ"ך', NOW()),
('source-3', 'ויקרא יט:יח', 'לֹא תִקֹּם וְלֹא תִטֹּר אֶת בְּנֵי עַמֶּךָ וְאָהַבְתָּ לְרֵעֲךָ כָּמוֹךָ אֲנִי יְהוָה', 'תנ"ך', NOW()),
('source-4', 'משנה אבות א:א', 'מֹשֶׁה קִבֵּל תּוֹרָה מִסִּינַי, וּמְסָרָהּ לִיהוֹשֻׁעַ, וִיהוֹשֻׁעַ לִזְקֵנִים, וּזְקֵנִים לִנְבִיאִים, וּנְבִיאִים מְסָרוּהָ לְאַנְשֵׁי כְנֶסֶת הַגְּדוֹלָה', 'משנה', NOW()),
('source-5', 'משנה אבות א:ב', 'שִׁמְעוֹן הַצַּדִּיק הָיָה מִשְּׁיָרֵי כְנֶסֶת הַגְּדוֹלָה. הוּא הָיָה אוֹמֵר, עַל שְׁלשָׁה דְבָרִים הָעוֹלָם עוֹמֵד, עַל הַתּוֹרָה וְעַל הָעֲבוֹדָה וְעַל גְּמִילוּת חֲסָדִים', 'משנה', NOW()),
('source-6', 'תלמוד בבלי ברכות ז:א', 'אמר רבי יוחנן משום רבי יוסי: טובה מרדות אחת בלבו של אדם יותר מכמה מלקיות', 'תלמוד', NOW()),
('source-7', 'רמב"ם הלכות תשובה א:א', 'כל מצות שבתורה בין עשה בין לא תעשה אם עבר אדם על אחת מהן בין בזדון בין בשגגה כשיעשה תשובה וישוב מחטאו חייב להתודות', 'הלכה', NOW()),
('source-8', 'מסילת ישרים פרק א', 'יסוד החסידות ושורש העבודה התמימה הוא שיתברר ויתאמת אצל האדם מה חובתו בעולמו', 'מוסר', NOW()),
('source-9', 'בעל שם טוב על פרשת בראשית', 'אמר הבעל שם טוב: האדם הוא עולם קטן, וכל מה שיש בעולם הגדול יש גם באדם', 'חסידות', NOW()),
('source-10', 'הרב קוק, אורות התשובה', 'התשובה היא תביעה הבאה מתוך החיים עצמם של היחיד ושל הציבור', 'מחשבה', NOW());
INSERT INTO insights (id, content, user_id, source_id, created_at) VALUES
('insight-1', 'רש"י מפרש "בראשית" - בשביל התורה שנקראת "ראשית דרכו", כלומר, העולם נברא בזכות התורה.', '00000000-0000-0000-0000-000000000001', 'source-1', NOW() - INTERVAL '2 day'),
('insight-2', 'הרמב"ן מפרש "בראשית" מלשון ראשית, כלומר, בתחילה ברא אלוקים את השמים ואת הארץ.', '00000000-0000-0000-0000-000000000002', 'source-1', NOW() - INTERVAL '1 day'),
('insight-3', 'אבן עזרא מסביר שהמצווה הראשונה היא להאמין באל אחד, ולכן נאמר "אנכי ה'' אלקיך" בלשון יחיד.', '00000000-0000-0000-0000-000000000001', 'source-2', NOW()),
('insight-4', 'הרמב"ם מונה את "ואהבת לרעך כמוך" כאחת מהמצוות החשובות ביותר, ורבי עקיבא אמר שזהו כלל גדול בתורה.', '00000000-0000-0000-0000-000000000003', 'source-3', NOW() - INTERVAL '5 day'),
('insight-5', 'משנה זו מדגישה את רעיון מסירת התורה שבעל פה מדור לדור, ומתארת את שרשרת המסורת.', '00000000-0000-0000-0000-000000000002', 'source-4', NOW() - INTERVAL '3 day'),
('insight-6', 'הרמב"ם מסביר שעל שלושת העמודים האלה - תורה, עבודה, וגמילות חסדים - מתקיים העולם כולו.', '00000000-0000-0000-0000-000000000003', 'source-5', NOW() - INTERVAL '2 day'),
('insight-7', 'רבנו יונה מפרש שמוסר עצמי ותיקון המידות חשובים יותר מעונשים חיצוניים.', '00000000-0000-0000-0000-000000000001', 'source-6', NOW() - INTERVAL '1 day'),
('insight-8', 'הרב סולובייצ''יק מסביר שהווידוי הוא חלק הכרחי בתהליך התשובה כי הוא מבטא את ההכרה בחטא.', '00000000-0000-0000-0000-000000000002', 'source-7', NOW()),
('insight-9', 'החפץ חיים הדגיש את חשיבות ההתבוננות בתכלית חיינו כפי שמופיע במסילת ישרים.', '00000000-0000-0000-0000-000000000003', 'source-8', NOW() - INTERVAL '4 day'),
('insight-10', 'האדמו"ר מפיאסצנא הסביר שכל אדם הוא עולם שלם, ולכן יש חשיבות עצומה לתיקון הפרט.', '00000000-0000-0000-0000-000000000001', 'source-9', NOW() - INTERVAL '3 day'),
('insight-11', 'הרב קוק רואה בתשובה לא רק תיקון לחטא, אלא תהליך של התעלות והתחדשות רוחנית מתמדת.', '00000000-0000-0000-0000-000000000002', 'source-10', NOW() - INTERVAL '2 day');
INSERT INTO comments (id, content, user_id, insight_id, created_at) VALUES
('comment-1', 'פירוש מרתק של רש"י! תודה רבה על השיתוף.', '00000000-0000-0000-0000-000000000003', 'insight-1', NOW() - INTERVAL '1 day'),
('comment-2', 'גם הזוהר מפרש את "בראשית" באופן דומה, המילה מרמזת על הספירות.', '00000000-0000-0000-0000-000000000002', 'insight-1', NOW()),
('comment-3', 'פירוש הרמב"ן על בראשית הוא אחד הפירושים העמוקים ביותר לתורה.', '00000000-0000-0000-0000-000000000001', 'insight-2', NOW() - INTERVAL '12 hour'),
('comment-4', 'מעניין לראות את ההבדל בין פרשנותו של רש"י לבין הרמב"ן בהקשר זה.', '00000000-0000-0000-0000-000000000003', 'insight-2', NOW() - INTERVAL '6 hour'),
('comment-5', 'אבן עזרא בדרך כלל מדגיש את הפשט, וכאן הוא מדגיש את הרעיון המונותאיסטי.', '00000000-0000-0000-0000-000000000002', 'insight-3', NOW() - INTERVAL '5 hour'),
('comment-6', 'הלל הזקן גם אמר "דעלך סני לחברך לא תעביד" - מה ששנוא עליך אל תעשה לחברך. זהו הפירוש לאהבת הרע.', '00000000-0000-0000-0000-000000000001', 'insight-4', NOW() - INTERVAL '3 day'),
('comment-7', 'מעניין לראות איך המשנה מתארת את תהליך העברת המסורת בצורה ברורה כל כך.', '00000000-0000-0000-0000-000000000003', 'insight-5', NOW() - INTERVAL '2 day'),
('comment-8', 'הווידוי הוא תהליך מטהר כי הוא מחייב את האדם להתעמת עם מעשיו באופן ישיר.', '00000000-0000-0000-0000-000000000001', 'insight-8', NOW() - INTERVAL '1 day'),
('comment-9', 'הרב סולובייצ''יק מדגיש שהתשובה היא יותר מאשר הבעת חרטה - היא שינוי מהותי באדם.', '00000000-0000-0000-0000-000000000003', 'insight-8', NOW()),
('comment-10', 'המסילת ישרים מתחיל בשאלה המהותית ביותר - מה חובת האדם בעולמו. זוהי נקודת התחלה מושלמת.', '00000000-0000-0000-0000-000000000002', 'insight-9', NOW() - INTERVAL '3 day');
