INSERT INTO sources (id, title, content, category, created_at) VALUES
('62f17a3d-7e3a-4e3f-9fcb-71cce1c67eff', 'בראשית א:א', 'בְּרֵאשִׁית בָּרָא אֱלֹהִים אֵת הַשָּׁמַיִם וְאֵת הָאָרֶץ', 'תנ"ך', NOW()),
('c0d83a30-77b2-4101-8087-45783b78475e', 'שמות כ:ב-ג', 'אָנֹכִי יְהוָה אֱלֹהֶיךָ אֲשֶׁר הוֹצֵאתִיךָ מֵאֶרֶץ מִצְרַיִם מִבֵּית עֲבָדִים. לֹא יִהְיֶה לְךָ אֱלֹהִים אֲחֵרִים עַל פָּנָי', 'תנ"ך', NOW()),
('d7b5e47a-82c9-4c6a-958c-bd2c2f0aca1b', 'ויקרא יט:יח', 'לֹא תִקֹּם וְלֹא תִטֹּר אֶת בְּנֵי עַמֶּךָ וְאָהַבְתָּ לְרֵעֲךָ כָּמוֹךָ אֲנִי יְהוָה', 'תנ"ך', NOW()),
('a8b9c2d3-e4f5-4a2b-b1c2-d3e4f5a6b7c8', 'משנה אבות א:א', 'מֹשֶׁה קִבֵּל תּוֹרָה מִסִּינַי, וּמְסָרָהּ לִיהוֹשֻׁעַ, וִיהוֹשֻׁעַ לִזְקֵנִים, וּזְקֵנִים לִנְבִיאִים, וּנְבִיאִים מְסָרוּהָ לְאַנְשֵׁי כְנֶסֶת הַגְּדוֹלָה', 'משנה', NOW()),
('7c8d9e0f-1a2b-3c4d-5e6f-7a8b9c0d1e2f', 'משנה אבות א:ב', 'שִׁמְעוֹן הַצַּדִּיק הָיָה מִשְּׁיָרֵי כְנֶסֶת הַגְּדוֹלָה. הוּא הָיָה אוֹמֵר, עַל שְׁלשָׁה דְבָרִים הָעוֹלָם עוֹמֵד, עַל הַתּוֹרָה וְעַל הָעֲבוֹדָה וְעַל גְּמִילוּת חֲסָדִים', 'משנה', NOW()),
('3f4a5b6c-7d8e-9f0a-1b2c-3d4e5f6a7b8c', 'תלמוד בבלי ברכות ז:א', 'אמר רבי יוחנן משום רבי יוסי: טובה מרדות אחת בלבו של אדם יותר מכמה מלקיות', 'תלמוד', NOW()),
('9a8b7c6d-5e4f-3a2b-1c0d-9e8f7a6b5c4d', 'רמב"ם הלכות תשובה א:א', 'כל מצות שבתורה בין עשה בין לא תעשה אם עבר אדם על אחת מהן בין בזדון בין בשגגה כשיעשה תשובה וישוב מחטאו חייב להתודות', 'הלכה', NOW()),
('5e6f7a8b-9c0d-1e2f-3a4b-5c6d7e8f9a0b', 'מסילת ישרים פרק א', 'יסוד החסידות ושורש העבודה התמימה הוא שיתברר ויתאמת אצל האדם מה חובתו בעולמו', 'מוסר', NOW()),
('1c2d3e4f-5a6b-7c8d-9e0f-1a2b3c4d5e6f', 'בעל שם טוב על פרשת בראשית', 'אמר הבעל שם טוב: האדם הוא עולם קטן, וכל מה שיש בעולם הגדול יש גם באדם', 'חסידות', NOW()),
('7a8b9c0d-1e2f-3a4b-5c6d-7e8f9a0b1c2d', 'הרב קוק, אורות התשובה', 'התשובה היא תביעה הבאה מתוך החיים עצמם של היחיד ושל הציבור', 'מחשבה', NOW());
